import { NgModule, ModuleWithProviders } from '@angular/core';
import { VERSION } from './version';
import { CommonModule } from '@angular/common';

import { RouterModule, Routes, Router, Route } from '@angular/router';

import { AppSettings } from '@ngscaffolding/models';



import {
  AppSettingsService,
  MenuService,
  LoggingService,
  VersionsService,
  DynamicComponentService
} from 'ngscaffolding-core';

import { InputBuilderModule } from 'ngscaffolding-inputbuilder';

import { DatagridModule } from 'ngscaffolding-datagrid';
import { MachineDetailsComponent } from './pages/Finder/MachineDetails/machineDetails.component';
import { DetailsListComponent } from './components/detailsList/detailsList.component';
import { UpgradeCalcComponent } from './components/upgradeCalc/upgradeCalc.component';

import { ButtonModule } from 'primeng/primeng';
import { CheckboxModule } from 'primeng/primeng';
import { CardModule } from 'primeng/primeng';
import { SharedModule } from 'primeng/primeng';
import { DialogModule } from 'primeng/primeng';
import { DropdownModule } from 'primeng/primeng';
import { EditorModule } from 'primeng/primeng';
import { FieldsetModule } from 'primeng/primeng';
import { InputTextModule } from 'primeng/primeng';
import { MultiSelectModule } from 'primeng/primeng';
import { PanelModule } from 'primeng/primeng';
import { ProgressSpinnerModule } from 'primeng/primeng';
import { SpinnerModule } from 'primeng/primeng';
import { TabMenuModule } from 'primeng/primeng';
import { TabViewModule } from 'primeng/primeng';
import { ScrollPanelModule } from 'primeng/primeng';

// export * from './pages';

const machineDetailsRoute: Route =  { path: 'fieldforcemachinedetails', component: MachineDetailsComponent, outlet: 'popup' };
const upgradeCalcRoute: Route =  { path: 'fieldforceupgradeCalc', component: UpgradeCalcComponent };

const appRoutes: Routes = [
  machineDetailsRoute,
  upgradeCalcRoute
];

@NgModule({
  imports: [
    CommonModule,
    InputBuilderModule,
    DatagridModule,
    ButtonModule,
    CardModule,
    CheckboxModule,
    SharedModule,
    DialogModule,
    PanelModule,
    DropdownModule,
    EditorModule,
    FieldsetModule,
    InputTextModule,
    MultiSelectModule,
    PanelModule,
    ProgressSpinnerModule,
    SpinnerModule,
    TabMenuModule,
    TabViewModule,
    ScrollPanelModule,
    RouterModule.forChild(appRoutes)
  ],
  declarations: [
    MachineDetailsComponent,
    DetailsListComponent,
    UpgradeCalcComponent
  ],
  exports: [
    // MachineDetailsComponent,
    RouterModule]
})
export class FieldForceAppModule {
  static forRoot(): ModuleWithProviders {
    return {
      ngModule: FieldForceAppModule
    };
  }

  constructor(
    appSettingsService: AppSettingsService,
    dynamicComponentService: DynamicComponentService,
    menuService: MenuService,
    logger: LoggingService,
    versions: VersionsService,
    router: Router
  ) {
    logger.info('Setting Values', 'FieldForce.startup');

    versions.addVersion('FieldForce', VERSION.version, true);

    appSettingsService.setValue(AppSettings.title, 'FieldForce Application');
    appSettingsService.setValue(AppSettings.iconUrl, 'data:image/png;base64,');
    // AppSettings.apiHome = 'http://essoft-vm.cloudapp.net:3000';
    // AppSettings.apiAuth = 'http://essoft-vm.cloudapp.net:3010';
    appSettingsService.setValue(AppSettings.apiHome, 'http://localhost:3000');
    appSettingsService.setValue(AppSettings.apiAuth, 'http://localhost:3010');
    appSettingsService.setValue(AppSettings.errorLogConsole, true);
    appSettingsService.setValue(AppSettings.errorLogServer, true);
    appSettingsService.setValue(AppSettings.errorShowUser, true);

    appSettingsService.setValue(AppSettings.inputShowCalendarIcon, true);

    appSettingsService.setValue(AppSettings.showFullMessages, false);
    appSettingsService.setValue(AppSettings.showToastMessages, true);

    appSettingsService.setValue(AppSettings.showProfileSetting, false);
    appSettingsService.setValue(AppSettings.showUserSetting, false);
    appSettingsService.setValue(AppSettings.showProfilePicture, false);

    appSettingsService.setValue(AppSettings.authClientId, 'democlient');
    appSettingsService.setValue(AppSettings.authClientSecret, 'democlientsecret');
    appSettingsService.setValue(AppSettings.authScope, 'offline_access openid');
    appSettingsService.setValue(AppSettings.authShowForgotPassword, true);
    appSettingsService.setValue(AppSettings.authShowRegister, true);
    appSettingsService.setValue(AppSettings.authShowRememberMe, true);
    appSettingsService.setValue(AppSettings.authSaveinLocalStorage, true);
    appSettingsService.setValue(AppSettings.authTokenEndpoint, '/auth/token');
    appSettingsService.setValue(AppSettings.authTermsAndConditions,
      'FieldForce Terms and Conditions Here. Lorem ipsum dolor sit amet, consectetur adipiscing elit.');

    dynamicComponentService.registerComponent(machineDetailsRoute);

    menuService.addMenuItemsFromCode([]);
  }
}
